import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EvidenceUpload from '../components/EvidenceUpload';
import ProgressUpdate from '../components/ProgressUpdate';
import KpiDetailModal from '../components/KpiDetailModal';
import './Staff.css';

function Staff() {
  const [kpis, setKpis] = useState([]);
  const [selectedKpi, setSelectedKpi] = useState(null);
  const [selectedKpiIndex, setSelectedKpiIndex] = useState(null);
  const [activeEvidenceIndex, setActiveEvidenceIndex] = useState(null);
  const [activeProgressIndex, setActiveProgressIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState('All');
  const [sortOption, setSortOption] = useState('None');

  const navigate = useNavigate();
  const currentStaffId = sessionStorage.getItem('staffId');

  useEffect(() => {
    if (!currentStaffId) return;

    fetch(`/api/kpi/staff/${currentStaffId}`)
      .then((res) => res.json())
      .then((data) => {
        const initialized = data.map((kpi) => {
          let status = kpi.status;
          if (kpi.progress === 100) status = 'Completed';
          else if (kpi.progress >= 60) status = 'On Track';
          else if (kpi.progress === 40) status = 'At Risk';
          else status = 'Behind';

          return {
            ...kpi,
            status,
            comments: kpi.comments || [],
            files: kpi.files || [],
          };
        });
        setKpis(initialized);
      })
      .catch((err) => console.error('Error loading KPIs:', err));
  }, [currentStaffId]);

  const handleProgressSubmit = (index, newValue) => {
    const updated = [...kpis];
    updated[index].progress = newValue;

    if (newValue === 100) updated[index].status = 'Completed';
    else if (newValue >= 60) updated[index].status = 'On Track';
    else if (newValue === 40) updated[index].status = 'At Risk';
    else updated[index].status = 'Behind';

    setKpis(updated);
    setActiveProgressIndex(null);
  };

  const handleEvidenceUpload = (index, file) => {
    const updated = [...kpis];
    updated[index].files = updated[index].files || [];
    updated[index].files.push(file);
    setKpis(updated);
  };

  const handleKpiSelection = (kpi, index) => {
    setSelectedKpi({ ...kpi });
    setSelectedKpiIndex(index);
  };

  const handleKpiDetailSubmit = (data) => {
    if (selectedKpiIndex === null) return;
    const updated = [...kpis];
    const kpi = updated[selectedKpiIndex];

    switch (data.type) {
      case 'progress':
        kpi.progress = data.progress;
        kpi.status =
          data.progress === 100
            ? 'Completed'
            : data.progress >= 60
              ? 'On Track'
              : data.progress === 40
                ? 'At Risk'
                : 'Behind';
        if (!kpi.comments) kpi.comments = [];
        if (data.comment) {
          kpi.comments.push({
            text: data.comment,
            date: new Date().toLocaleString(),
            progress: data.progress,
          });
        }
        break;

      case 'evidence':
        if (data.evidence?.length > 0) {
          kpi.files = [...(kpi.files || []), ...data.evidence];
        }
        break;

      case 'submit':
        kpi.progress = 100;
        kpi.status = 'Completed';
        kpi.submitted = true;
        kpi.verifyStatus = 'Pending';
        if (!kpi.comments) kpi.comments = [];
        if (data.comment) {
          kpi.comments.push({
            text: data.comment,
            date: new Date().toLocaleString(),
            progress: 100,
            isFinal: true,
          });
        }
        break;
    }

    setKpis(updated);
    setSelectedKpi({ ...kpi });
  };

  let filteredKpis = kpis
    .map((kpi) => {
      const updated = { ...kpi };
      if (kpi.progress === 100 && kpi.status !== 'Completed') {
        updated.status = 'Completed';
      }
      if (kpi.verifyStatus === 'Accepted') {
        updated.title += ' Verified';
      }
      return updated;
    })
    .filter((kpi) => {
      const matchesFilter =
        filterStatus === 'All' ||
        (filterStatus === 'On Track' &&
          kpi.status === 'On Track' &&
          kpi.progress < 100) ||
        (filterStatus === 'Needs Attention' &&
          (kpi.status === 'Behind' || kpi.status === 'At Risk')) ||
        (filterStatus === 'Completed' && kpi.progress === 100) ||
        (filterStatus === 'Verified' && kpi.verifyStatus === 'Accepted') ||
        (filterStatus === 'Incomplete' && kpi.progress < 100);

      const matchesSearch =
        kpi.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        kpi.description.toLowerCase().includes(searchQuery.toLowerCase());

      return matchesFilter && matchesSearch;
    });

  if (sortOption === 'Due Date') {
    filteredKpis.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
  } else if (sortOption === 'Progress') {
    filteredKpis.sort((a, b) => b.progress - a.progress);
  }

  const totalKpis = kpis.length;
  const onTrack = kpis.filter((k) => k.status === 'On Track').length;
  const needsAttention = kpis.filter(
    (k) => k.status === 'Behind' || k.status === 'At Risk'
  ).length;
  const completed = kpis.filter((k) => k.progress === 100).length;
  const incomplete = kpis.filter((k) => k.progress < 100).length;
  const verified = kpis.filter((k) => k.verifyStatus === 'Accepted').length;

  return (
    <div className="staff-container">
      <h3 className="staff-heading">My KPI</h3>

      <div className="summary-cards">
        <div className="summary-card">
          <div className="summary-card-title">Total KPIs</div>
          <div className="summary-card-value total-kpis">{totalKpis}</div>
        </div>
        <div className="summary-card">
          <div className="summary-card-title">On Track</div>
          <div className="summary-card-value on-track">{onTrack}</div>
        </div>
        <div className="summary-card">
          <div className="summary-card-title">Needs Attention</div>
          <div className="summary-card-value at-risk">{needsAttention}</div>
        </div>
        <div className="summary-card">
          <div className="summary-card-title">Completed</div>
          <div className="summary-card-value completed-kpi">{completed}</div>
        </div>
        <div className="summary-card">
          <div className="summary-card-title">Incomplete</div>
          <div className="summary-card-value incomplete-kpi">{incomplete}</div>
        </div>
        <div className="summary-card">
          <div className="summary-card-title">Verified</div>
          <div className="summary-card-value verified-kpi">{verified}</div>
        </div>
      </div>

      <div className="search-filters">
        <input
          type="text"
          className="search-input"
          placeholder="Search KPIs..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div className="filter-group">
          {['All', 'On Track', 'Needs Attention', 'Completed', 'Verified'].map(
            (status) => (
              <button
                key={status}
                className={`filter-btn ${filterStatus === status ? 'active' : ''
                  }`}
                onClick={() => setFilterStatus(status)}
              >
                {status}
              </button>
            )
          )}
        </div>
        <select
          className="sort-select"
          value={sortOption}
          onChange={(e) => setSortOption(e.target.value)}
        >
          <option value="None">Sort By</option>
          <option value="Due Date">Due Date</option>
          <option value="Progress">Progress</option>
        </select>
      </div>

      <div className="row">
        {filteredKpis.map((kpi, index) => (
          <div className="col-6" key={index}>
            <div className="kpi-card">
              <div className="kpi-header">
                <h6 className="kpi-title">
                  {kpi.title}
                  {kpi.verifyStatus === 'Accepted' && (
                    <span className="verification-badge verified">âœ“ Verified</span>
                  )}
                </h6>
                <span
                  className={`status-chip ${kpi.progress === 100
                      ? 'completed'
                      : kpi.status.replace(/ /g, '-').toLowerCase()
                    }`}
                >
                  {kpi.progress === 100
                    ? 'COMPLETED'
                    : kpi.status.toUpperCase()}
                </span>
              </div>

              <p className="kpi-description">{kpi.description}</p>

              <div className="progress-container">
                <div
                  className={`progress-bar ${kpi.progress === 100 ? 'success' : 'info'
                    }`}
                  style={{ width: `${kpi.progress}%`, color: '#fff' }}
                >
                  {kpi.progress}%
                </div>
              </div>

              <div className="kpi-footer">
                <div className="kpi-meta">
                  Due: {kpi.dueDate?.slice(0, 10)}{' '}
                  {kpi.verifyStatus && (
                    <span
                      className={`verification-badge ${kpi.verifyStatus.toLowerCase()}`}
                    >
                      {kpi.verifyStatus}
                    </span>
                  )}
                </div>
                <button
                  className="btn btn-outline-primary btn-small"
                  onClick={() => handleKpiSelection(kpi, index)}
                >
                  View KPI
                </button>
              </div>

              <EvidenceUpload
                show={activeEvidenceIndex === index}
                onClose={() => setActiveEvidenceIndex(null)}
              />
              <ProgressUpdate
                show={activeProgressIndex === index}
                currentProgress={kpi.progress}
                onClose={() => setActiveProgressIndex(null)}
                onSubmit={(value) => handleProgressSubmit(index, value)}
              />
            </div>
          </div>
        ))}
      </div>

      <KpiDetailModal
        show={selectedKpi !== null}
        onClose={() => {
          setSelectedKpi(null);
          setSelectedKpiIndex(null);
        }}
        onSubmit={handleKpiDetailSubmit}
        kpiDetails={selectedKpi}
      />
    </div>
  );
}

export default Staff;
